
// Показать и скрыть часть текста  jquery
$(document).ready(function(){
	$('.content_toggle').click(function(){
		$('.content-block').toggleClass('hide');	
		if ($('.content-block').hasClass('hide')) {
      $(".arrow-t").css("transform", "rotate(360deg) translateX(-50%)");
		} else {
			$(".arrow-t").css("transform", "rotate(180deg) translateX(50%)");
		}		
		return false;
	});				
});